ifb = lambda l : l[0] | (l[1] << 8)

def get_ch(ch):
    oc = ord(ch)
    ioff = 2 * (oc - 32 + 1) if oc >= 32 and oc <= 126 else 0
    doff = ifb(_mvi[ioff : ])
    width = ifb(_mvfont[doff : ])

    next_offs = doff + 2 + ((width - 1)//8 + 1) * 21
    return _mvfont[doff + 2:next_offs], 21, width
 
def getWidthArray():
    widthArray = []
    for i in range (32, 127):
        ioff = 2 * (i - 32 + 1) if i >= 32 and i <= 126 else 0
        doff = ifb(_mvi[ioff : ])
        width = ifb(_mvfont[doff : ])
        widthArray.append(width)
    return widthArray